  chavó: ['m. ochavo.'],
  chavo: ['m. ochavo.'],
  checa: ['f. Comité de policía secreta de la antigua Unión Soviética.', 'Organismo semejante que ha funcionado en otros países y que sometía a torturas a los detenidos.'],
  checo: ['adj. y s. De la República Checa o relativo a ella: la capital checa es Praga.', 'De la antigua Checoslovaquia o relativo a ella, checoslovaco.'],
  chele: ['adj. y com. amer. [Persona] muy blanca o rubia.', 'm. amer. Legaña.'],
  cheli: ['m. Jerga madrileña que contiene elementos castizos,  marginales y contraculturales.'],
  chelo: ['m. violonchelo.'],
  chepa: ['f. Corcova,  joroba.', 'adj. y com. Jorobado.'],
  chica: ['adj. Pequeño,  de poco tamaño.', 'adj. y s. Niño,  muchacho.'],
  chico: ['adj. Pequeño,  de poco tamaño.', 'adj. y s. Niño,  muchacho.'],
  chifa: ['m. col. amer. Restaurante chino.'],
  chile: ['m. Ají,  pimiento muy picante: el chile es un condimento muy empleado en la cocina mexicana.', 'amer. col. Mentira. Más en pl.'],
  china: ['f. Piedra pequeña y,  a veces,  redondeada: tengo una china en el zapato.', 'En la jerga de la droga,  barra de hachís prensado: me han pasado una china de primera.'],
  chiné: ['adj. [Tela] rameada o de varios colores combinados: ¿dónde vas con vestido chiné?'],
  chino: ['m. Piedrecita, china 1.', 'En la jerga de la droga,  heroína para inhalar que se quema sobre papel de plata.'],
  chipa: ['f. amer. Torta de harina de maíz o mandioca y queso.'],
  chipá: ['f. amer. Torta de harina de maíz o mandioca y queso.'],
  chipe: ['adj. col. Estupendo,  muy bueno: pasamos un rato chipén.'],
  chipé: ['adj. col. Estupendo,  muy bueno: pasamos un rato chipén.'],
